import React, { useState } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "../style/Map.css";
import "leaflet/dist/leaflet.css";
import Geolocate from "./GeoLocate";
import TextField from "@material-ui/core/TextField"


function Map() {
  const [position, setPosition] = useState([37.7492, -97.7201]);

  const parentGeolocateFunction = (geo) => {
    var newPosition = [
      geo.coords.latitude + Math.random() * 50,
      geo.coords.longitude + Math.random() * 50,
    ]
    setPosition(...newPosition);
  };

  return (
    <div className="map">
      <Geolocate parentGeolocateFunction={parentGeolocateFunction} />
      <MapContainer center={position} zoom={5} zoomControl={false} minZoom={5}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
      </MapContainer>
      <TextField required id="standard-required" label="Required" defaultValue={position} />

    </div>
  );
}

export default Map;
