import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "../style/Map.css";
import "leaflet/dist/leaflet.css";
import SearchContainer from "./SearchContainer";
import L from 'leaflet'


function Map() {
  const [position, setPosition] = useState([37.7492, -97.7201]);

  const parentGeolocateFunction = (geo) => {
    setPosition([geo.coords.latitude + (Math.random()*100), geo.coords.longitude]);
    
  };

  useEffect(() => {

    L.map('map', {
      center: [49.8419, 24.0315],
      zoom: 16,
      layers: [
        L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}.png', {
          attribution:
            '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        }),
      ]
    });


  })
  console.log(position)

  return (
    <div className="map">
      <SearchContainer parentGeolocateFunction={parentGeolocateFunction} />
      {/* <MapContainer
        center={position}
        zoom={5}
        zoomControl={false}
        minZoom={5}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
      </MapContainer> */}
    </div>
  );
}

export default Map;
