import React, {useState} from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "../style/Map.css";
import "leaflet/dist/leaflet.css";
import SearchContainer from "./SearchContainer";

function Map() {
  const [position, setPosition] = useState({latitude: 37.7492, longitude: -97.7201})

  const parentGeolocateFunction = (geo) => {
    
    // setPosition({
    //   latitude: geo.latitude,
    //   longitude: geo.longitude,
    // });
    console.log(position)
    console.log(geo)
  };

  return (
    <div className="map">
      <SearchContainer parentGeolocateFunction={parentGeolocateFunction} />
      <MapContainer center={[position.latitude, position.longitude]} zoom={5} zoomControl={false} minZoom={5}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
      </MapContainer>
    </div>
  );
}

export default Map;
